mapper.select:
SELECT COUNT(*)
FROM hometown_association
WHERE association_name = #{associationName}

SELECT COUNT(*)
FROM hometown_association
WHERE association_id = #{associationId}

SELECT COUNT(*)
FROM association_place
WHERE association_id = #{associationId}
  AND place = #{place}

mapper.delete:
DELETE
FROM association_place
WHERE association_id = #{associationId}
  AND place = #{place}

mapper.select:
SELECT place
FROM association_place
WHERE association_id = #{associationId}
LIMIT #{startIndex}, #{pageSize}

SELECT COUNT(*)
FROM association_place
WHERE association_id = #{associationId}

SELECT COUNT(*)
FROM ticket
WHERE state = #{state}

mapper.update:
UPDATE ticket
SET state = 2
WHERE ticket_id = #{ticketId}

UPDATE ticket SET seat_surplus = #{newSeatSurplus},
                  version = version + 1
              WHERE ticket_id = #{ticketId}
              AND version = #{version}

UPDATE ticket SET seat_surplus = seat_surplus + #{travelNum},
                  version = version + 1
              WHERE ticket_id = #{ticketId}

mapper.select:
SELECT departure_time AS departureTime,departure_place AS departurePlace,
        destination, travel_num AS travelNum,price,passenger,phone
        FROM travel_order t, association_order a
        WHERE t.travel_order_id = #{travelOrderId}
        AND t.travel_order_id = a.travel_order_id;
