mapper.select:
SELECT COUNT(*)
FROM hometown_association
WHERE association_name = #{associationName}

SELECT COUNT(*)
FROM hometown_association
WHERE association_id = #{associationId}

SELECT COUNT(*)
FROM association_place
WHERE association_id = #{associationId}
  AND place = #{place}

mapper.delete:
DELETE
FROM association_place
WHERE association_id = #{associationId}
  AND place = #{place}

mapper.select:
SELECT place
FROM association_place
WHERE association_id = #{associationId}
LIMIT #{startIndex}, #{pageSize}

SELECT COUNT(*)
FROM association_place
WHERE association_id = #{associationId}
